def ismostlymagicsquare(arr):
    leftdiag=0
    rightdiag=0
    n=len(arr)
    #to check diagonals 
    for i in range(n):
        leftdiag +=arr[i][i]
        rightdiag+=arr[i][n-i-1]
    if leftdiag != rightdiag:
        return False
    
    #To check row and col
    for i in range(n):
        row=0
        col=0
        for j in range(n):
            row+=arr[i][i]
            col+=arr[j][i]
            
        if row!= leftdiag and col!=leftdiag:
            return False
    return True
