def smallestdifference(a):
    if(len(a)==0):
        return -1
    a.sort()
    l=[]
    dif=0
    
    for i in range(len(a)):
        
        for j in range(i+1,len(a)):
            dif=abs(a[i]-a[j])
            l.append(dif)        
    # your code goes here
    return min(l)
